//
//  The aqua-colored bars are the same color the whole time, as "proven" when they animate
//  together and the stripes are removed. Code golfing encouraged! Inspired by this video:
//
//      https://youtu.be/QTC-0F3gJhQ?si=cCPUi2ueBIwROnxW
// ---------------------------------------------------------------------------------------

void mainImage(out vec4 O, vec2 u)
{
    u /= iResolution.xy;

    vec4 p = vec4(.9,.3,.9,0),
         o = vec4(.9,.6,.2,0),
         g = vec4(.5, 1,.6,0);

    float t = 1. - abs(fract(iTime / 8.) - .5) * 2.,
          a = clamp(t, .5, .75) - .5,
          b = clamp(t, .75, 1.) - .75;

    O = fract(u.x * 45.) > .5 ?
        abs(u.y - .3 - .4 * a) < .1 ?
            t * 4. < u.x ? p : g :
            u.y > .5 && u.y < .5 + .8 * b ? g : p :
        abs(u.y - .7 + .4 * a) > .1 ?
            u.y > .5 - .8 * b && u.y < .5 ? g : o :
            t * 4. < 2. - u.x ? o : g;
}
