// CC0: Simple gradient background for Windows Terminal
//  https://mrange.github.io/windows-terminal-shader-gallery/

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/iResolution.xy;

  vec2 p = -1.0 + 2.0*q;

  // Compute a simple gradient
  vec3 col = vec3(.0, q).zxy;
  // Reduce intensity of gradient more to the corners
  col *= 0.71*smoothstep(1.75, 0., length(p));

  fragColor = vec4(col,1.0);
}
