void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // define the position of the current pixel
    vec2 pos = fragCoord.xy / iResolution.xy;
    pos -= vec2(0.5, 0.5);
    pos.x *= iResolution.x/iResolution.y;

    // calculate the angle of rotation based on the current time
    float angle = iTime * 4.0;

    // rotate the position around the center of the screen
    pos = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * pos;
    pos += vec2(0.5, 0.5);

    // test whether the current pixel is inside the rectangle
    if (pos.x > 0.25 && pos.x < 0.75 && pos.y > 0.25 && pos.y < 0.75) {
        // set the color of the pixel to red
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    }
    else {
        // set the color of the pixel to black
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
