// golfing 496 chars https://shadertoy.com/view/MdSfWK


void mainImage( out vec4 O, vec2 U )
{
    O = vec4(1);                                                      // start plain white
    for (int k=0; k<289; k++) {                                       // check 17x17 neighborhood
        vec2 P = ceil( U/4. + vec2(k%17,k/17) - 9. );                 // cell Id
        P += fract( 1e4* sin( P * mat2(12.1,-37.4,-17.3,31.7) )) -.5; // random point in cell
        P *= 4.;
        float v = texture(iChannel0, P/iResolution.xy ).r ,           // check texture there
              r = 2.+30.*v;                                           // circle radius
        fract( 1e4* sin( dot(P,vec2(12.1,31.7))  )) * r/8. < 1.-v     // drawing proba ~ darkness/circumference
          ?  O -= .2 * smoothstep(1.5,0.,abs( length(P-U) - r )) :O;  // draw antialiased circle
    }
}
