void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv = (fragCoord/iResolution.xy) - vec2(0.5);
    vec2 uv2 = (fragCoord/iResolution.xy) - vec2(0.5,0.505);
    vec2 uv3 = (fragCoord/iResolution.xy) - vec2(0.505,0.5);
    float Circle = (length(uv) + (5.*(iTime*-0.02))) * 40.;
    float Circle2 = (length(uv2) + (5.*(iTime*-0.02))) * 40.;
    float Circle3 = (length(uv3) + (5.*(iTime*-0.02))) * 40.;

    float Cc = (sin(Circle)+1.)/2.;
    float Cc2 = (sin(Circle2)+1.)/2.;
    float Cc3 = (sin(Circle3)+1.)/2.;
    float intensity = 10.;
    float Cy = (Cc-Cc2) * intensity;
    float Cx = (Cc-Cc3) * intensity;

    vec3 Result = normalize(cross(vec3(1,0,Cy),vec3(0,1,Cx)));
    Result *= vec3(1.,1.,Result.z*10.);
    fragColor=vec4(vec3(Result),1);
}
