const float power = 3.;
const float phi = 1.61803398875;
const float pi = 3.1415926;
const float tau = pi*2.;
const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}
//hash by IQ https://www.shadertoy.com/view/XlXcW4

//*
vec2 circleSample(uvec3 x){
    vec2 v = hash(x).xy;
    return sin(v.x * tau + vec2(0,11))*sqrt(mod(v.x*1e2,1.));
}
