void mainImage( out vec4 O, in vec2 U )
{
    vec2 r = iResolution.xy;
    vec2 uv = U/r;    
    O=O-O;

    float b = .1*pow(abs(uv.y - .5 - sin(iTime)*.3),2.);
    int s = int(ceil(float(2<<11)*sqrt(b))),i=s;

    for(;i-->0;){
        /*
        vec2 p = circleSample(uvec3(iFrame+i, int(U.x)+i, int(U.y)*i));/*/
        vec2 p = bokehSample(i,s);//*/
        p.y *= r.x/r.y;
        vec4 c = texture(iChannel0, uv+p*b);
        c *= c*2.;
		O += pow(c,power+O-O);
	}
	O /= float(s);
    O = pow(O, 1./power+O-O);
}

void mainImage( out vec4 O, in vec2 U )
{
    int s = int(mod(iTime*0.4, 4.));
    vec2 uv = U/iResolution.xy;

    float d = 0.5;
    uv.x = uv.x * (mix(1., uv.y, d)) + (1.-uv.y)*d*.5;

    O = 
     s < 1?texture(iChannel0,uv.yx)
    :s < 2?texture(iChannel1,uv.yx)
    :s < 3?texture(iChannel2,uv)
    :      texture(iChannel3,uv);
}
