void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float left= (cos(iTime - uv.x*2.0)+3.0)*0.2;
    float fade= (sin(iTime - uv.x*2.0)+3.0)*0.2;

        if( left > uv.y){
        fragColor = vec4(1,1,0,0)*fade;
    }else{
        fragColor = vec4(0, 1, 2, 0)*fade;
    }

}
