const float PI = 3.1415926538;
const float TAU = 6.2831853071;

const vec3 RED = vec3(1, 0, 0);
const vec3 GRN = vec3(0, 1, 0);
const vec3 BLU = vec3(0, 0, 1);

float remap(float a, float b, float x, float y, float v) {
    return (v - a) / (b - a) * (y - x) + x;
}

float aa_step(float threshold, float x) {
    float pixelSize = 1.5 / iResolution.y;
    return smoothstep(threshold - pixelSize, threshold + pixelSize, x);
}

float star_dist(vec2 p, int sides, float angle) {
    float pAngle = atan(p.y, p.x);
    float shape = sin(pAngle * float(sides) + angle * float(sides));
    return remap(-1., 1., .75, 1., shape);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - .5 * iResolution.xy) * 2. / iResolution.y;
    float d = length(uv);
    float t = iTime;

    float r = t * TAU * .1;
    float poffset = (1./3.) * TAU;
    float pscale = .1;

    vec3 color = vec3(0);
    color += RED * aa_step(d, star_dist(uv, 6, r + sin(t + poffset * 0.) * pscale));
    color += GRN * aa_step(d, star_dist(uv, 6, r + sin(t + poffset * 1.) * pscale));
    color += BLU * aa_step(d, star_dist(uv, 6, r + sin(t + poffset * 2.) * pscale));

    vec3 srgb = pow(color, vec3(1./2.2));
    fragColor = vec4(srgb, 1.0);
}
