void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float th = 0.05;
    vec2 uv = fragCoord / iResolution.xy;
    float smoothError = smoothstep(th, th*.9, abs(uv.y - (sin( (uv.x*15.+iTime))/2.+.5) ));
    //smoothError += smoothstep(th, th*.9, abs(uv.y - (sin( ((uv.x-th/4.)*15.+iTime))/2.+.5) ));
    //smoothError += smoothstep(th, th*.9, abs(uv.y - (sin( ((uv.x+th/4.)*15.+iTime))/2.+.5) ));
    fragColor = vec4( vec3(smoothError, 0.0, smoothError*.05), 1.0); 

}
