  #define f(x)  .5+.5*sin(x)
//#define f(x)  step(.5,fract((x)/6.))

#define F(y,x,v)  vec4( f( (y)/75. - (abs(x) > 55. ? sign(x):0.) - v*iTime ) )
                          // ./75 = *3./R.y in icon size 

#define band(x,v) O = abs(x) < 60.                   \
                        ? abs(U.y) < R.y/3.          \
                          ?   F( 0.  , x,v)          \
                          : U.y > 0.                 \
                            ? F( U.y , x,v)          \
                            : F(   x , x,v) : O 

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 2.*u - R;

    O = vec4(.5);
    band(U.x+400., 40.);
    band(U.x+100., 20.);
    band(U.x-100.,-20.);
    band(U.x-400.,-10.);
 // O = ( .6 + .6 * cos( 6.3*O.x  + vec4(0,23,21,0)  ) );  // uncomment to see in chrominance rather than luminance
}
