        vec4 pos_vel = texture(iChannel1, fragCoord/iChannelResolution[1].xy);
        pos_vel.xy = iResolution.xy / 2.0 + (pos_vel.xy - 0.5) * iResolution.xy * 0.2;
        pos_vel.zw = (pos_vel.zw - 0.5) * iResolution.xy * 1.0;
