float remap(float t, float fX, float fY, float tX, float tY)
{
    return ((t-fX)/(fY-fX))*(tY-tX) + tX;
}

mat2 rotate(float a)
{
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float circle(vec2 uv, float r)
{
    float d = length(uv);
    return smoothstep(r, r-0.001, d);
}

float band( float t, float s, float e, float b)
{
    float s1 = smoothstep(s-b, s+b, t);
    float s2 = smoothstep(e+b, e-b, t);

    return s1 * s2;
}

float lineBand (vec2 uv)
{
    vec2 rot1 = uv * rotate(2. * iTime);
    float b1 = band(rot1.x, -0.3, 0.3, 0.001);

    vec2 rot2 = uv * rotate(-2. * iTime);
    float b2 = band(rot2.y, -0.3, 0.3, 0.001);

    float f = b1 * b2;

    float e = sin(0.5 + iTime * 1.) * 0.5 ;
    e = remap(e , 0., 0.5, 0.33, 0.34);
    float c = circle(uv, e);
    f *= c;

    return f;
}

vec3 lineCircle(vec2 uv, vec2 pos)
{
    uv -= pos;

    float l1 = lineBand(uv);
    uv /= 0.98;
    float l2 = lineBand(uv);

    float f = l1 - l2;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 colf = vec3(f) * col;

    return colf;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 c1 = vec3(0.);

    for(float i = -0.5; i <= 0.5; i +=.125)
    {
        vec3 c = lineCircle(uv, vec2(i, 0.));
        c1 += c;
    }


    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col -= c1;

    fragColor = vec4( col , 1.0);
}
