// Fork of "rhombus stripes" by secondhandgnome. https://shadertoy.com/view/dltSW2
// 2023-06-03 14:06:32

#define PI     3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Create uv, which is the coordinate in clip space.
    // Clip space ranges from 0 to 1.
    // Equivalent to vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y)
    vec2 uv = fragCoord / iResolution.xy;

    float rippleAmplitude = (1.0 / 64.0);
    float rippleFreq = 8.0;

    float xCenter = 0.5 + rippleAmplitude * sin(rippleFreq * PI * uv.y);
    float yCenter = 0.5 + rippleAmplitude * sin(rippleFreq * PI * uv.x);
    float muteFactor = 0.9;
    float stripeSpeed = 10.0;
    float stripePeriod = 10.0;
    float stripeWidth = 5.0;
    float dEdgeX = 1.0-2.0*abs(xCenter - uv.x);
    float dEdgeY = 1.0-2.0*abs(yCenter - uv.y);
    float dEdge = (dEdgeX + dEdgeY) / 2.0;

    vec4 stripeShade = vec4(0, 0, 0, 0);
    if (mod(100.0 * dEdge + iTime * stripeSpeed, stripePeriod) < stripeWidth) {
        stripeShade = vec4(1, 1, 1, 0.5);
    }

    fragColor = vec4(dEdgeX * muteFactor, dEdgeY * muteFactor, dEdge * muteFactor, 1) * stripeShade;   
}
