void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float uvMin = 2.;
    float uvMax = 15.;
    float uvSpeed = 0.5;
    float exposure = 0.04;

    vec2 uv = fragCoord/iResolution.xy;

    vec2 uv1 = (fragCoord/iResolution.xy * 2. - 1.) * (sin(uvSpeed * iTime) * (uvMax - uvMin)/2. + uvMin * 1.5);
    uv1.x *= iResolution.x / iResolution.y;

    vec2 uv2 = (fragCoord/iResolution.xy * 2. - 1.) * (sin(uvSpeed * iTime + 0.1) * (uvMax - uvMin)/2. + uvMin * 1.5);
    uv2.x *= iResolution.x / iResolution.y;

    vec2 uv3 = (fragCoord/iResolution.xy * 2. - 1.) * (sin(uvSpeed * iTime + 0.2) * (uvMax - uvMin)/2. + uvMin * 1.5);
    uv3.x *= iResolution.x / iResolution.y;

    float x3 = smoothstep(pow(abs(uv.x - 0.5) * abs(uv.y - 0.5)-exposure * sin(iTime-0.8), 0.2), 0.1, sin(iTime)/10.+0.25);

    float x1r = pow(sin(length(fract(uv1*x3)*2.-1.)), 20.);
    float x1g = pow(1.05 * sin(length(fract(uv2*x3)*2.-1.)), 20.);
    float x1b = pow(1.1 * sin(length(fract(uv3*x3)*2.-1.)), 20.);

    fragColor = vec4(x1r,x1g,x1b,0.);
}
