void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Check Basic Patterns 1: https://www.shadertoy.com/view/dtVGDt
    vec2 normCoord = fragCoord/iResolution.xy;
    vec2 uv = -1. + 2. * normCoord;
    float time = iTime/2.;

    // Check Basic patterns 2: https://www.shadertoy.com/view/mtVGDt
    uv.x *= iResolution.x/iResolution.y;
    float radius = length(uv);
    float rings = sin(time - radius * 5.);

    // Check Basic patterns 3: https://www.shadertoy.com/view/mtVGDd
    float radar = sin(atan(uv.x,uv.y) + time);

    // Combining the previous patterns and adding
    // time creates this swirl-like effect.
    float swirl = sin(rings + radar + time);

    // Output color to screen
    fragColor = vec4(rings, radar, swirl, 1.);
}
