#ifdef GL_ES
precision mediump float;
#endif

vec2 rand(vec2 p){
    float a=dot(p,vec2(0.530,-0.190));
    float b=dot(p,vec2(-0.170,0.490));
	return vec2(fract(sin(a*1043.231)*2301.085),fract(cos(b*1022.708)*4271.589))*2.-1.;
}
float gradient_noise(vec2 st){
    vec2 i=floor(st);
    vec2 j=smoothstep(0.,1.,fract(st));
    return mix(mix(dot(st-i,rand(i)),dot(st-(i+vec2(1,0)),rand(i+vec2(1,0))),j.x),
    mix(dot(st-(i+vec2(0,1)),rand(i+vec2(0,1))),dot(st-(i+vec2(1)),rand(i+vec2(1))),j.x),j.y)*0.5+0.5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    fragColor = vec4(vec3(gradient_noise(st*10.)),1.0);
}
