vec3 palette(float t){
    vec3 a = vec3(.5,.5,.5);
    vec3 b = vec3(.5,.5,.5);
    vec3 c = vec3(1.,1.,1.);
    vec3 d = vec3(.263,.416,.557);

    return a + b*cos(6.28318*(c*t*d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord.xy*2. - iResolution.xy)
    /iResolution.y;
    vec2 uv0 = uv;

    vec3 finalColor = vec3(0.);

    float d = abs(length(uv)+1.25) * 
        exp(fract(length(uv0)*3.5));

    vec3 col = palette(length(uv0) + iTime*4.);

    d = sin(d*1.5 + iTime)/9.;
    uv = fract(uv*1.5) - .5;

    d = pow(.05/d, 1.2);

    d *= length(uv)*2.;

    finalColor += col * d;

    fragColor = vec4(finalColor,1.);
}
