 vec4 boxblur(float size, vec2 uv)
 {
     size = floor(size * 10.0);
     vec4 result = vec4(0.0,0.0,0.0,1.0);
     for (float x = -size; x <=size; x+=1.0)
     {
         for (float y = -size; y <=size; y+=1.0)
         {
            result+=texture(iChannel0, vec2(uv.x + x/iResolution.x, uv.y + y/iResolution.y));
         }
     }
     float c = ( size * 2.0 + 1.0);
     return result/( c*c);
 }


 void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 m = iMouse/iResolution.xyxy;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 r = iResolution.xy; 
    vec4 col = (step(m.x,uv.x)  == 1.0) ? texture(iChannel0, uv): boxblur(m.y, uv);
    if(m.z > 0.0){
    if (abs(m.y - uv.y) < 1.0/iResolution.y) {col = vec4(1.0,1.0,1.0,1.0);}}
    if (abs(m.x - uv.x) < 1.0/iResolution.x) {col = vec4(1.0,1.0,1.0,1.0);}
    fragColor = col;
}
