void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = 28. * (fragCoord - .5*iResolution.xy) / iResolution.y;
    vec2 ipos = floor(uv);

    fragColor = (mod(ipos.x,28.)>14. && mod(ipos.x,14.)>0. && mod(ipos.y-iTime,14.)>4.) ||
        (mod(ipos.x,28.)<14. && mod(ipos.x,14.)>0. && mod(ipos.y+iTime,14.)>4.) ? vec4(.098) : vec4(.365,.737,.976,1.);
}
