// Infinite TV wall zoom
// by Ruud Helderman, November 1, 2023 - MIT License

#define R 160.   // resolution: how many images make up one image, both horizontally and vertically

#define T(z) texture(iChannel0, round(fract((c / iResolution.xy - .5) * pow(R, z) + .5) * R) / R)

void mainImage(out vec4 o, vec2 c)
{
    float z = fract(2. * acos(.96 * cos(.1 * iTime)) - .7);    // alternately zoom in and out
    //float z = fract(.2 * iTime);     // zoom out forever
    //float z = fract(-.2 * iTime);    // zoom in forever

    o = mix(T(z), T(z - 1.), z);
}
