//original work: https://www.shadertoy.com/view/7sf3z2
float map(vec2 p) {
    return length(p) - 0.2;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy * 4.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 col = vec3(0.0); 
    float time = iTime * 0.25;
    float frequency = 1.0;  // color frequency
    for(float j = 0.0; j < 3.0; j++) {
        for(float i = 1.0; i < 8.0; i++) {
            uv.x += (0.2 / (i + j) * sin(i * atan(iTime) * 2.0 * uv.y + (iTime * 0.1) + i * j));
            uv.y += (1.0 / (i + j) * cos(i * 0.6 * uv.x + (iTime * 0.25) + i * j));
            // motion
            float angle = iTime * 0.1;
            mat2 rotation = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
            uv = rotation * uv;
        }
        vec3 newColor = vec3(
            0.5 * sin(frequency * uv.x + time) + 0.5,
            0.5 * sin(frequency * uv.y + time + 2.0) + 0.5,
            sin(frequency * (uv.x + uv.y) + time + 4.0)
        );

newColor = pow(newColor, vec3(2.0));  // sharpnesss
        col += newColor;
    }
        col /= 3.0;
    vec3 bg = vec3(0.0, 0.0, 0.0); //for darkness =)
    col = mix(
        col,
        bg,
        1.0 - smoothstep(0.1, abs(sin(iTime * 0.05) * 3.0), map(uv))
    );
    fragColor = vec4(col, 1.0);
}
