void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float color = fract(uv.y * 0.6 + iTime * 1.0);

    fragColor = vec4(fract(color * 1.00000000),
                     fract(color * 0.00000000),
                     fract(color * 2.00000000),
                     fract(color * 3.00000000));

}
