#define PI 3.141592

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv * 2.0 - 1.0;

    float a = acos(dot(vec2(1.0, 0.0), normalize(uv)));

    if (uv.y < 0.0) a = 2.0 * PI - a;

    a /= (PI * 2.0);

    float l = length(uv);

    uv.y = pow(l, -1.21) + iTime;
    uv.x = a * 8.0;

    fragColor = texture(iChannel0, uv);
    fragColor *= min(1.0, (max(0.0, l - 0.1)));
}
