void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 col = vec3(1.0);
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    float d = length(uv);
    if (d > 0.5) col = vec3(0.0);

    vec2 uv2 = fragCoord.xy / iResolution.xy;
    vec3 col2 = vec3(0.0);
    uv2 = uv2 * 2.0 - 1.0;
    uv2.x *= iResolution.x / iResolution.y;
    uv2.x -= 0.2;
    float d2 = length(uv2);
    if (d2 > 0.25) col2 = vec3(1.0);

    vec2 uv3 = fragCoord.xy / iResolution.xy;
    vec3 col3 = vec3(0.0);
    uv3 = uv3 * 2.0 - 1.0;
    uv3.x *= iResolution.x / iResolution.y;
    uv3.x -= 0.2;
    float d3 = length(uv3);
    float t = iTime * 0.5;
    float y = sin(uv3.x * 10.0 + t) * 0.5 + 0.5;
    if (d3 > 0.25 && d3 < 0.25 + y * 0.25) col3 = vec3(0.0, 1.0, 0.0);
    fragColor = vec4(col * col2 * col3, 1.0);

}
