#define T(a) abs(fract(1.5 * (a + 1./vec2(3,2)))-.5)

void mainImage( out vec4 O, vec2 U )
{      
    vec2 u = (U/iResolution.y + (12.+iTime) * vec2(.1, .04)) / 6e2,
         v;

    O = vec4(2,3,3,0);

    for(float i; i++ < 7.;)
        u = abs(u + v + .5),      
        u = u.y > u.x ? u.yx + vec2(0, 1.25)
                      : u + .15 * u * i,           
        v = -T(u+.5),
        u = -abs(v-T(u.yx)),
        O = .1 + 4. * exp(O * (u.x+u.y));

    O /= 3.;
}
