#define PI 3.14159265359
#define t iTime
#define res iResolution

const float freq = 3.523;

const vec3 deepSea = vec3(0.18, 0.25, 0.25);
const vec3 sageClay = vec3(0.86, 0.89, 0.80);
const vec3 sand = vec3(0.86, 0.73, 0.56);

float wave(in vec2 uv, in float mult) 
{
    uv += sin(uv.y*PI*1.5 + t)*(mult/30.);
    float col = (fract(sin(uv.x*PI*freq*sin(t*mult*0.1)*.5+1.)));
    return col;//float(ceil(col*mult*10.6)/(mult*10.6));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col;

    col = mix(deepSea, vec3(0.87, 0.96, 0.95)*1.01, wave(uv,  0.8));
    col = mix(sageClay, col, wave(uv, 0.321));
    col = mix(col, sand, wave(uv, 0.3)*0.2);

    fragColor = vec4(col,1.0);
}
