#define R vec3(1.000,0.369,0.273);
#define G vec3(0.581,1.000,0.652);
#define B vec3(0.301,0.725,1.000);
#define O 1./3.

vec3 rgb(float x) {
    if (x < O) return R;
    if (x > O*2.) return B;
    return G;
}

vec3 effect2(vec2 uv, float w) {
    vec3 color = vec3(1.);
    float abs = clamp(uv.x-w*2., 0., w*3.)/w;

    float t = 3.6;
    if (fract(iTime*4.) > .5) t = 300.; 
    abs = fract(t*abs+iTime);
    color = rgb(abs);

    float f = sin(uv.y*15.-uv.x*100.+iTime*2.);
    if (1.-f < .2) {
        color = vec3(0.);
    }
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    vec2 copy = uv;
    uv = fract(uv+iTime/10.);

    vec3 color = vec3(1.);

    float w = .17;
    // float spacing = .2;

    if (uv.x < w) {
        float t = 600.;
        if (fract(iTime) > .5) t = 300.; 
        float f = sin(uv.y*t+iTime*10.);
        color = vec3(f);
    }

    if (uv.x > w*2. && uv.x < w*3.) {
        float f = sin(uv.x*200.+iTime*2.);
        color = vec3(f);

        if (uv.y < .5 && uv.y > .1) {
            color = effect2(uv, w);
        }
    }

    if (uv.x > w*4. && uv.x < w*5.) {
        float f = sin(uv.y*2.*uv.x*100.+iTime*22.);
        color = vec3(f);
        if (1.-f < .2) {
            color = vec3(f-.2)+vec3(0.,0.,1.);
        }
    }

    if (color.z+color.y+color.z == 3.0) {
        // color = R;
        uv = fract(copy+iTime/10.*uv.y);
        if (fract(iTime) > .5) {
        	uv *= w-cos(iTime)+fract(uv.x*12.)-fract(uv.y*12.);
        	uv /= 10.;
        }

        if (uv.x < w) {
            float f = sin(uv.y*600.+iTime*10.);
            color = vec3(f);
    	}

    if (uv.x > w*2. && uv.x < w*3.) {
        float f = sin(uv.x*200.+iTime*2.);
        color = vec3(f);

        if (uv.y < .5 && uv.y > .1) {
            color = effect2(uv, w);
        }
    }

        if (uv.x > w*4. && uv.x < w*5.) {
            float f = sin(uv.y*2.*uv.x*100.+iTime*22.);
            color = vec3(f);
            if (1.-f < .2) {
                color = vec3(f-.2)+vec3(0.,0.,1.);
            }
        }

        // color *= R-G-B-10.;
        color /= 2.;
    }

    // color = vec3(st.x,st.y,abs(sin(u_time)));

    fragColor = vec4(color,1.0);
}
