#define PI 3.141592
 //((p.x >= 0.) ? 1. : -1.)
float dTriangle(vec2 p)
{
    p.y += 0.1;
    float theta = asin(p.y/length(p));
    if(theta >= 0. && theta < PI/4.)
        return (((p.x >= 0.) ? 1. : -1.)*1./3.*p.x+p.y);
    else if(theta >= PI/4. && theta < PI/2. )
        return (((p.x >= 0.) ? 1. : -1.)*3.*p.x+p.y)/3.;
    else if(theta >= -PI/4. && theta < 0. )
        return (((p.x >= 0.) ? -1. : 1.)*1./3.*p.x+p.y);
    else
        return (((p.x >= 0.) ? -1. : 1.)*3.*p.x+p.y)/3.;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = (uv - 0.5) *2.;

    pos.x *= iResolution.x/iResolution.y;


    //float d = pos.y/length(pos);
    float d = dTriangle(pos);

    d = abs(d);

    float x = 10.*d -5.*iTime;

    d = sin(x);

    d = abs(d);

    //d = step(0.5,d);
    //d = 0.01 / d;


    vec3 l[3];
    l[0] = vec3(1,0,0);
    l[1] = vec3(0,1,0);
    l[2] = vec3(0,0,1);
    // Time varying pixel color
    vec3 col = step(0.9, d) == 1. ? l[int(abs(x)/PI) % 3] : vec3(0,0,0);


    fragColor = vec4(col, 1.);
}
