void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pi = 3.1415;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float val1 = (cos(pi*(uv.x-0.5))+1.0)/2.0;
    float val2 = (cos(pi*(uv.y-0.5))+1.0)/2.0;

    float a = 1.8;

    float r = 1.0*mod(iTime/a+700.0*val1*val2, 1.0);
    float g = 1.0*mod(iTime/a+1000.0*val1*val2, 1.0);
    float b = 1.0*mod(iTime/a+500.0*val1*val2, 1.0);
    vec3 col = vec3(0, g, b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
