int iterations = 5000;
float threshold = 1000.0;
float baseScale = 5.0;
vec2 centerPos = vec2(-1.5013347, 0.0009049);
float colourConstant = 10.0;

vec3 get_col(float iter) {
    vec3 col;

    float val = mod(iter, colourConstant)/colourConstant;
    int val2 = int(floor(mod(iter, colourConstant*3.0)/colourConstant));
    switch (val2) {
        case 0: col = vec3(val, 0.0, 1.2-val); break;
        case 1: col = vec3(1.2-val, val, 0.0); break;
        case 2: col = vec3(0.0, 1.2-val, val); break;
    }

    return col;
}

vec3 get_mandelbrot(vec2 c) {
    vec2 z = c;
    bool check = true;
    float iter = 0.0;
    for(int i; i<iterations; i++) {
        vec2 m = z;
        m.x = (z.x * z.x) - (z.y * z.y) + c.x;
        m.y = 2.0 * z.x * z.y + c.y;
        z = m;
        if (abs(z.x) > float(threshold) || abs(z.y) > float(threshold)) {
            check = false;
            break;
        } iter = float(i);
    }

    vec3 col;
    if (check) {
        col = vec3(0.0, 0.0, 0.0);
    } else {
        col = get_col(iter);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime+6.0;
    float scale = baseScale / pow(t/10.0, t);
    vec2 uv = (fragCoord/iResolution.xy*scale)-(scale/2.0) + centerPos;
    uv.y /= iResolution.x / iResolution.y;
    vec3 col = get_mandelbrot(uv);

    fragColor = vec4(col,1.0);
}
