void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float dst = length(uv);

    float mask = smoothstep(0.1, 6.0, dst);

    // outside
    float pulse = sin(dst * 13.0 - iTime) * 20. / 0.1;
    pulse = abs(pulse) * mask;

    // center
    float center = smoothstep(0.49, 0.55, dst);

    // Combine
    float finalValue = mix(center, pulse, smoothstep(0.5, 0.55, dst));

    vec3 color = vec3(finalValue, finalValue, finalValue);
    fragColor = vec4(color, 1.0);
}
