void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel0,fract(uv));

    vec4 smpl = texture(iChannel0,fract(uv*iResolution.xy));
    float avg = (smpl.r + smpl.g + smpl.b) / 3.;
    avg *= 2.;

    if(avg > 1.)
    fragColor /= avg;
    else
    fragColor *= avg;
}
