void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor=vec4(1.0,1.0,1.0,1.0);

    if(uv.y>0.5*uv.x*cos(iTime)+0.1 && uv.y<(-uv.x)+0.9 && uv.x>0.2) fragColor=vec4(0.1*cos(iTime)+0.8,0.1*cos(iTime)+0.6,1.0*cos(iTime)-0.3,1.0);

    if ((uv.x-1.0*cos(iTime))*(uv.x-1.0*cos(iTime))<=0.0125*0.9*cos(iTime) && (uv.y-0.7)*(uv.y-0.7)<=0.0125*2.4*cos(iTime)) 

    fragColor=vec4(1.0,cos(iTime)*uv.y,0.0,1.0);

    if (uv.y<0.6667-0.5*cos(iTime) && uv.y>0.3333-0.5*cos(iTime) && uv.x>0.5-0.5*cos(iTime) && uv.x<0.8333-0.5*cos(iTime)) fragColor=vec4(0.5*(-cos(iTime))*uv.x,-cos(iTime)*uv.x,0.5,1.0);

}
