#ifdef GL_ES
precision mediump float;
#endif


vec3 hash3( vec2 p )
{
    vec3 q = vec3( dot(p,vec2(127.1,311.7)), 
                   dot(p,vec2(269.5,183.3)), 
                   dot(p,vec2(419.2,371.9)) );
    return fract(sin(q)*43758.5453);
}

float voronoise( in vec2 p, int seed, float randomness, float u, float v )
{
    float k = 1.0+63.0*pow(1.0-v,6.0);

    vec2 i = floor(p);
    vec2 f = fract(p);

    vec2 a = vec2(0.0,0.0);
    for( int y=-2; y<=2; y++ )
    for( int x=-2; x<=2; x++ )
    {
        vec2  g = vec2( x, y );
        vec3  o = hash3( i + g )*vec3(u,u,1.0);
        vec2  d = g - f + o.xy;
        float w = pow( 1.0-smoothstep(0.0,1.414,length(d)), k );
        a += vec2(o.z*w,w);
    }

    return a.x/a.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xx;

    // Set the variable values directly in the code
    float frequency = 50.0;       // Adjust the frequency of the pattern
    float sharpness = 1.0;        // Adjust the sharpness of the pattern
    float irregularity = 0.0;     // Adjust the irregularity of the pattern
    int seed = 1;                 // Adjust the seed for randomization
    float randomness = 100.0;       // Adjust the randomness of the pattern

    vec2 p = vec2(sharpness, irregularity);
    float f = voronoise( frequency * uv, seed, randomness, p.x, p.y );

    fragColor = vec4( f, f, f, 1.0 );
}
