vec3 Color(vec2 uv) {
  uv+=0.5;
  vec3 c1=vec3(1.2, 0.8, 0.7);
  vec3 c2=vec3(1., 0.8, 1.);
  return mix(c1, c2, uv.x);
}

float heart(vec2 uv, float size, vec2 offset) {
  float x=uv.x-offset.x;
  float y=uv.y+offset.y;
  float d=(x*x+y*y-size)*(x*x+y*y-size)*(x*x+y*y-size) - (x*x*0.4)*(y*y*y*1.4);
  return d;
}

float circle(vec2 uv, float r, vec2 offset) {
  float x=uv.x-offset.x;
  float y=uv.y-offset.y;
  return length(vec2(x, y*1.5))-r;
}

float square(vec2 uv, float size, vec2 offset) {
  float x=uv.x+offset.x;
  float y=uv.y+offset.y;
  return max(abs(x), abs(y*4.0))-size;
}

vec3 drawScene(vec2 uv) {
  vec3 col=Color(uv);
  float circle=circle(uv, 0.2*cos(iTime+1.0), vec2(0.7*cos(iTime+3.0), 0.2));
  float square=square(uv, 0.3, vec2(0.3*cos(iTime+1.0), 0.3+0.1*sin(iTime+5.0)));
  float heart=heart(uv, 0.03, vec2(0.03));
  col=mix(vec3(0.9, 0., 0.7), col, step(0.04, circle));
  col=mix(vec3(1.2, 0.5, 1.), col, step(0., heart));
  col=mix(vec3(0.7, 0.4, 0.9), col, step(0., square));
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
  vec2 uv=fragCoord/iResolution.xy;
  uv-=0.5;
  uv.x*=iResolution.x/iResolution.y;
  vec3 col=drawScene(uv);
  fragColor=vec4(col,0.0);
}
