const float PI = 3.14;

float animateSine(float pos, float freq, float speed){
    return (sin((pos * freq -iTime * speed)) + 1.0) * 0.5;
}



float makeSine(vec2 uv, float offset){

    uv.y += offset * 10.0;
    float baseWave = sin(uv.x * 0.03 + offset * 0.001 * uv.x) * 20.0;
    float animate = animateSine(uv.x, 0.003, 3.0);

    float animatedCol = animate * (3.0 * animate - abs(uv.y + baseWave));

    return max(0.0,animatedCol);
}


float drawWaves(vec2 fragCoord, int numberOfWaves, float freq){
    float waves = 0.0;
    for(int i = 0;i < numberOfWaves;i++){
       waves += makeSine(fragCoord, float(i) * freq);
    }
    return waves * 0.2;
}



mat2 rotationMatrix(float angle)
{
	angle *= PI / 180.0;
    float s=sin(angle), c=cos(angle);
    return mat2( c, -s, 
                 s,  c );
}

vec2 rotateCoord(vec2 coord, float rotation){
    return coord * rotationMatrix(rotation);
}

vec2 transposeCoord(vec2 coord, vec2 offset, float rotation){
    return coord * rotationMatrix(rotation) + offset;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);

    fragColor += drawWaves(transposeCoord(fragCoord, vec2(0,-300), -40.1), 25, 0.6);
    fragColor += drawWaves(transposeCoord(fragCoord, vec2(0,-200), 20.0), 40, 0.4);
        fragColor += drawWaves(transposeCoord(fragCoord, vec2(0,-200), 160.0), 20, 0.8);
}
