#define pow2(x) (x * x)

const float pi = atan(1.0) * 4.0;
const int samples = 16;
const float sigma = float(samples) * 0.25;
const float lightThreshold = 0.4;

float Gaussian(vec2 i) 
{
    float powSigma = 2.0 * pow2(sigma);
    return 1.0 / (pi * powSigma) * 
           exp(-(pow2(i.x) + pow2(i.y)) / powSigma);
}

float Graylize(vec3 color)
{
    float gray = dot(color, vec3(0.11, 0.59, 0.3));
    return max(gray - lightThreshold, 0.0);
}

vec3 blur(vec2 uv, vec2 uvUnit) 
{
    vec3 col = vec3(0.0);
    float accum = 0.0;
    float weight;
    vec2 offset;

    for (int x = -samples / 2; x < samples / 2; ++x) 
    {
        for (int y = -samples / 2; y < samples / 2; ++y) 
        {
            offset = vec2(x, y);
            weight = Gaussian(offset);
            col += Graylize(texture(iChannel0, uv + uvUnit * offset).rgb) * weight;
            accum += weight;
        }
    }
    return col / accum;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec2 uvUnit = 2.0 / iResolution.xy;
    float bloom = blur(uv, uvUnit).r;
    fragColor.rgb = texture(iChannel0, uv).rgb + bloom * vec3(0.82, 0.69, 0.375); //d2 b1 60
}
