// CLICK AND DRAG THE SCREEN IN THE Y DIRECTION TO ZOOM IF YOU CANT SEE!

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    // Constants
    int ITERATIONS = 1000;
    float THRESHOLD = 8.;

    // Coordinate change
    float a = -2.;
    float b = 0.;
    float c = -1.1;
    float d = 1.1;

    uv.x = (uv.x*abs(a-b))+a;
    uv.y = (uv.y*abs(c-d))+c;


    // Zoom uv
    if(iMouse.xy!=vec2(0., 0.)){
        uv *= iMouse.y/iResolution.y*2.;
    }



    //Z0
    vec2 z = vec2(0., 0.);
    float age = 0.;

    // Current pixel in mandelbrot?
    bool is_bounded = true;
    for(int i=0; i<ITERATIONS; i++){
        if(z.x*z.x + z.y*z.y > THRESHOLD){
            is_bounded = false;
            break;
        }

        // Calculate Zn+1
        vec2 z_new;

        z_new.x = z.x*z.x - z.y*z.y;
        z_new.y = 2.*z.x*z.y;

        z_new += uv;

        // Update z
        z = z_new;

        age++;
    }

    // Pixel color
    vec3 col = vec3(0.);
    if(!is_bounded){
        col = vec3(pow(age/float(ITERATIONS)*10., 0.75));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
