void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 color1 = vec3(1.0, 0.0, 0.0);
    vec3 color2 = vec3(0.0, 1.0, 0.0);
    vec3 color3 = vec3(1.0, 0.0, 1.0);
    vec3 color4 = vec3(0.0, 0.0, 1.0);

    vec3 top = mix(color1, color2, uv.x);
    vec3 bottom = mix(color3, color4, uv.x);

    vec3 color = mix(bottom, top, uv.y);

    // Convert from linear to 2.2 gamma
    color.r = pow(color.r, 1./2.2);
    color.g = pow(color.g, 1./2.2);
    color.b = pow(color.b, 1./2.2);

    fragColor = vec4(color, 1.0);
}
