void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5 * iResolution.xy)/iResolution.y;//centers uv
    uv *= 5.;//embiggens uv
    float t = iTime; //time
    vec3 pal = vec3(sin(t)-.95,.3,.2);//pallette experiment
    vec3 col = vec3(1.);//set to white
    float d = smoothstep(.9,.89, abs(uv.x) + fract(uv.y + t * 5.) * .5 + length(uv) + floor(uv.x * 2.));
    float e = smoothstep(.9,.09, fract(length(uv + sin(t))));
    float f = smoothstep(.9,.89, abs(uv.x * sin(t)));
    float g = smoothstep(.9,.89, cos(sin(uv.y) + t));
    float h = abs(uv.x + uv.y);
    col *= d;//d is spinning thingee
    col -= pal;//subtractive coloring using pallette
    col *= e;//e is saucer in middle
    col *= f;//f is vertical black bars      F and G may be backwards, I'm sleepy.
    col *= g;//g is horizontal black bars
    //col += h;
    fragColor = vec4(col,1.0);
}
