const float PI = 3.14159;
const float TWO_PI = 6.28318;

vec3 palette( in float t )
{
    vec3 a = vec3(0.821,0.328,0.242);
    vec3 b = vec3(0.659,0.481,0.896);
    vec3 c = vec3(0.612,0.340,0.296);
    vec3 d = vec3(2.820,3.026,-0.273);
    return a + b*cos(TWO_PI*(c*t+d));
}

float sdBox( in vec2 p, in vec2 b)
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float pulse(float frequency, float off) {
    return 0.5*(1.0+sin(TWO_PI * frequency * iTime + off));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0, 0.0, 0.0);


    for (float i = 0.0; i < (2.0 * PI); i+=(PI/9.0))
    for (float j = 0.0; j < 3.0; j++)
    {
    {
        float a = iTime * 1.2 + j * PI;
        float sc = 0.6 * pulse(0.2, j) + 0.2;
        vec2 off = vec2(uv.x + cos(i + a * 0.3) * sc, uv.y + sin(i + a) * sc);
        float d = length(off) - 0.01 * sc;

        vec3 c = palette(length(uv0) + iTime*0.3 + i*0.3);
        d = pow(0.015 / d, 1.6);

        finalColor += c * d;
    }
    }

    fragColor = vec4(finalColor, 1.0);
}
