void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,h; vec3 p;//q=vec3(-1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=(cos(iTime*.5));

    for(j=0.;j++<8.;)
        p=abs(abs(mod(cross(p,vec3(-1)),i/j*2.+iTime))-.03)-.05,
        d=dot(length(p.xy),p.z);
    e+=d;
}o+=vec4(-log(p*p),1)*8./i;
}
