void mainImage(out vec4 O, vec2 I)
{
    O *= 0.;
    vec2 r = iResolution.xy;
    for(float i;i<6.28;
        O+=(cos(i+vec4(0,2,4,6))+1.)*r.y/1e3/
        length(I+.4*r*abs(mod((iTime-9.)*
        cos((i+=.02)+vec2(0,11))/r*r.y-1.,4.)-2.)-r*.9));
    O = tanh(O*O);
}
