void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float y = -1.0;

    if(uv.x > 0.47 && uv.x < 0.49)
    {
        y = texture(iChannel0, vec2(0.07, 0.5)).x;
    }

    if(uv.x > 0.51 && uv.x < 0.53)
    {
        y = texture(iChannel1, vec2(0.07, 0.5)).x;
    }

    fragColor = (y == -1.0) ? vec4(0) : vec4(step(uv.y, y), 0.0, 1.0, 1.0);
 }
