void addDot(out vec3 color, in vec2 fragCoord, in vec2 uvPosition, in float uvSize) {
    vec2 uv = fragCoord / iResolution.xy * 2.0 - vec2(1.0, 1.0) + uvPosition;
    uv.x = uv.x * iResolution.x / iResolution.y;
    float power = uvSize / length(uv);

    float white = power * 0.33 * atan(power * uvSize);
    color = vec3(0.3 * power, 0.0, power) + vec3(white);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec3 composite = vec3(0.0);
    vec3 tmp = vec3(0.0);

    vec2 position = vec2(sin(iTime * 0.1), sin(iTime * 0.02));
    addDot(tmp, fragCoord, position, 0.5);
    composite += tmp;

    position = vec2(0.8 * sin(2.0 + iTime * 0.03), 0.6 * sin(7.2 + iTime * 0.2));
    addDot(tmp, fragCoord, position, 0.3);
    composite += tmp;

    position = vec2(tan(4.0 + iTime * 0.05), sin(7.2 + iTime * 0.06));
    addDot(tmp, fragCoord, position, 0.1);
    composite += tmp;

    position = vec2(0.5 * sin(4.0 - iTime * 0.025), 0.33 * sin(2.2 + iTime * 0.012));
    addDot(tmp, fragCoord, position, 0.175);
    composite += tmp;


    fragColor = vec4(composite, 1.0);
}
