float circle(vec2 p, vec2 centre, float r) {
  p -= centre;
  return length(p) - r;
}

float xor(float d0, float d1) {
  return min(max(d0,d1),max(-d0,-d1));
}

vec2 dup(vec2 p, float k) {
  return mod(p+k,2.0*k)-k;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  float scale = 10.0;
  if (key(CHAR_Z)) scale *= 2.0;
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  p *= scale;
  float px = 0.5*fwidth(p.x);
  float d = 1e8;
  float t = -log2(length(p));
  float loop = 64.0;
  t += mod(0.5*iTime,loop);
  t = round(t);
  int t0 = int(mod(t,loop));
  t -= mod(0.5*iTime,loop);
  float r = exp2(-t);
  float k = 0.25;
  k *= 2.0*float(ihash(uint(t0)))/exp2(32.0);
  k = exp2(k);
  float e = circle(p,vec2(0),r/k);
  float f = circle(p,vec2(0),r*k);
  d = min(d,max(f,-e));
  vec3 fg = vec3(0.5);
  vec3 bg = vec3(1);
  if (!key(CHAR_C) && d < 0.0) {
    float h = float(ihash(uint(t0^1)))/exp2(32.0);
    float inc = iMouse.x <= 0.0 ? 0.5 : iMouse.x/iResolution.x;
    float s = iMouse.x <= 0.0 ? 1.0 : iMouse.y/iResolution.y;
    fg = hsv2rgb(h,s,1.0); bg = hsv2rgb(h+inc,s,1.0);
  }
  vec2 p1 = dup(p,1.0);
  float d1 = circle(p1,vec2(0),1.0);
  d = xor(d,d1);
  float s = max(abs(p.x),abs(p.y));
  s -= 2.0;
  s *= 0.25;
  s = round(s);
  s /= 0.25;
  s += 2.0;
  e = max(abs(p.y)-s+1.0,abs(p.x)-s+1.0);
  f = max(abs(p.y)-s-1.0,abs(p.x)-s-1.0);
  d = xor(d,max(f,-e));
  vec3 col = mix(fg,bg,smoothstep(-px,px,d));
  col *= smoothstep(0.0,2.0*px,abs(d)-0.01);
  float dir = mod(0.5*dot(round(p-p1),vec2(1)),2.0) < 0.5 ? -1.0 : 1.0;
  if (length(p1) < 1.0) col *= 0.5-0.5*cos(0.3*PI*d1/px+2.0*dir*(PI*iTime+atan(p1.y,p1.x)));
  col = pow(col,vec3(0.4545));
  fragColor = vec4(col,1);
}

const int CHAR_A = 65;
const int CHAR_C = 67;
const int CHAR_X = 88;
const int CHAR_Y = 89;
const int CHAR_Z = 90;

#define key(code) (texelFetch(iChannel3, ivec2((code),2),0).x != 0.0)

const float PI = 3.141592654;

vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing       
  return v * mix( vec3(1.0), rgb, s);
}

// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint ihash(uint x) {
  x += 0x12345678u;
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}
