float sdEquilateralTriangle(in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p=vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}

vec3 wine_red=vec3(0.57,0.18,0.26);
vec3 warm_beige=vec3(0.9,0.78,0.65);
vec3 red_apple=vec3(0.88,0.32,0.41);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 col;
    float d;
    float AA=3.0/min(iResolution.x, iResolution.y);
    for(float i=0.0;i<15.0;i++)
    {
        for(float j=0.0;j<15.0;j++)
        {
            d=sdEquilateralTriangle(p+vec2(i,j)*0.3-1.8,0.25);
            col =  mix(col, vec3(1.0), smoothstep(AA,0.0,abs(d)));
        }
    }
    fragColor = vec4(col,1.0);
}
