#define R iResolution
#define L 15.

 float line(vec2 st, float pct) {
     return step(0. + pct, st.x) - step(0. + pct, st.y) ;
    }

 float line2(vec2 st, float pct) {
   return smoothstep(0., pct, st.x) * smoothstep(pct, pct + 0.4, st.y);
 }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord - 0.5 * R.xy) / R.y;

    float t;

    for (float i; i<L; i++) {
      t+= line(uv, i/ L);
      t+=line(-uv, i/L);



    }

    vec3 color = vec3(0.6 * sin(iTime / 5.), 0.3, 0.4 * sin(iTime)) ;
         color = color * t * sin(iTime / 10.);






    // Output to screen
    fragColor = vec4(color,1.0);
}
