const float   a   = 1./3.;
const float   b   = 1./6.;
const float scale =  2.5 ;


vec3 palette(float t) {

    vec3 a = vec3( .2,  .7,     .4    );
    vec3 b = vec3( .5,  .8,     .5    );
    vec3 c = vec3(1. , 2.,     1.     );
    vec3 d = vec3( .0,  .33333, .66666);

    return a + b*cos(6.28318*(c*t + d));
}



vec3 grayScale(float t) {

    return vec3(t,t,t);
}


float f(float x, float y, float iTime) { 

  float d =  x*x + y*y;
  float n=0.;

  float pa=a*log(iTime/3.);
  float pb=b*log(iTime/5.);


  for(int i=0; i<100; i++) {

      float newX = ( x  +  pa/d)*x - x*y;
	  float newY = (2.*x + pb/d)*y + x*y;  

	  x = newX;
	  y = newY;	  
	  n+=1.;

      // sentinela
      //if(n>10.) break;

      d = x*x + y*y;
      if(d >= 9.) break;
  }

  return (1.-(cos(n/922.1)+1.)*5.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = scale*(fragCoord/iResolution.xy - .5);

    float x = uv.x;
    float y = uv.y;


    float valxy = f(y, x, iTime);

    //vec3 col = grayScale(valxy);
    //vec3 col = palette(valxy);
    vec3 col = palette(pow(1.-valxy,5.)) * (grayScale(valxy)) ;


    // Output to screen
    fragColor = vec4(col,1.0);
}
